// import logo from './logo.svg';
import "./App.css";
import "../src/normal.css";
import {useState} from 'react';

function App() {

  const [input, setInput] =useState("");
  const [chatLog,setChatLog]=useState([{
    user:"gpt",
    message:"how can i help you today" 
  },{
    user:"me",
    message:"I want to use chatgpt today"
  }
  ]);

  async function handlesubmit(e){
   e.preventDefault();
   setChatLog([...chatLog, {user:"me" ,message:`${input}`}])
   setInput("");

  }
  return (
    <div className="App">
      <aside className="sidemenu">
        <div className="side-menu-button">
          <span>+</span>
          new chat
        </div>
      </aside>
      <section className="chatbox">
        <div className="chat-log">
          {
            chatLog.map((message, index)=>(
              <ChatMessage key={index} message ={message} />
            ))
          }
          {/* <div className="chat-message">
            <div className="chat-message-center">
              <div className="avatar"></div>
              <div className="message">hello world</div>
            </div>
          </div> */}

          {/* <div className="chat-message chatgpt">
            <div className="chat-message-center">
              <div className="avatar chatgpt">
                <svg xmlns="http://www.w3.org/2000/svg">
                  <title>{"Rectangle 5"}</title>
                  <path fill="#063855" fillRule="evenodd" d="M0 0h48v1H0z" />
                </svg>
              </div>
              <div className="message">i am supriya maurya</div>
            </div>
          </div> */}
        </div>

        <div className="chat-input-holder">
          <form onSubmit={handlesubmit}>
          <input rows="1" 
          value={input} 
          onChange = {(e) => setInput(e.target.value)} 
          className="chat-input-textarea"></input></form>
        </div>
      </section>
    </div>
  );
}

const ChatMessage =({message})=>{
   return(
    <div className={`chat-message ${message.user === "gpt" && "chatgpt"}`}> 
    <div className="chat-message-center">
      <div className={`avatar ${message.user === "gpt" && "chatgpt"}`}></div>
      <div className="message">{message.message}</div>
    </div>
  </div>
   )
}

export default App;
